import React, { Component } from 'react';
import Link from 'react-router-dom';
import './product.css';
import Cart from './cart';

const Product = (props) => {
    return(
        <React.Fragment>
            <div className="body">
            <div className="container">
            <div className="row justify-content-center text-center">
                {props.products.map(product => (
                        <div className="col-lg-3 col-md-3 col-sm-3" key={product.id}> 
                            <div id="review-slider-holder">
                                <ul id="light-slider">
                                    <li className="card">
                                        <div className="card-body">
                                            <img className="card-img" src={product.photo} alt=""/>
                                            <h6 className="card-title">{product.name}</h6>
                                            <span className="card-price">{product.price} LE</span>
                                            <br/>
                                            <button className='addbtn'>Add to cart <Cart onClick={props.onClick} product={product} /></button>
                                        </div>
                                    </li>
                                </ul>
                            </div>
                        </div>
                ))}
                    <div className="col-lg-3 col-md-3 col-sm-3"> 
                        <Link className="linknav" to="/product">Get more.</Link>
                    </div>
            </div>
        </div>
                
