import React, { Component } from 'react';
import cover0 from "../img/cover0.jpg";
import cover1 from "../img/cover1.jpg";
import cover2 from "../img/cover2.jpg";
import './home.css';

class Home extends Component {
    state = { 
        slideIndex : 1
     }

     
     plusSlides = (n) =>{
       showSlides(state.slideIndex += n);
     }
     
     currentSlide = (n) =>{
       showSlides(state.slideIndex = n);
     }
     
     showSlides = (n) =>{
        var i;
        var slides = document.getElementsByClassName("mySlides");
        var dots = document.getElementsByClassName("dot");
        if (n > slides.length) {
            slideIndex = 1
        }    
        if (n < 1) {
            slideIndex = slides.length}
        for (i = 0; i < slides.length; i++) {
            slides[i].style.display = "none";  
        }
        for (i = 0; i < dots.length; i++) {
            dots[i].className = dots[i].className.replace(" active", "");
        }
        slides[slideIndex-1].style.display = "block";  
        dots[slideIndex-1].className += " active";
        }
    render() { 
        return ( 
            <React.Fragment>
                {/* <div className="slidershow middle">
                    <div className="slides">
                        <input type="radio" name="r" id="r0" checked/>
                        <input type="radio" name="r" id="r1"/>
                        <input type="radio" name="r" id="r2"/>

                        <div className="slide s1">
                            <img src={cover0} alt=""/>
                        </div>
                        <div className="slide">
                            <img src={cover1} alt=""/>
                        </div>
                        <div className="slide">
                            <img src={cover2} alt=""/>
                        </div>
                    </div>
                    <div className="navigation">
                        <label htmlFor="r0" className="bar"></label>
                        <label htmlFor="r1" className="bar"></label>
                        <label htmlFor="r2" className="bar"></label>

                    </div>
                </div> */}
                {showSlides(state.slideIndex)}
                <div className="slideshow-container">

                    <div className="mySlides fade">
                        <div className="numbertext"></div>
                            <img src={cover0} style="width:100%"/>
                        <div className="text">Caption Text</div>
                    </div>

                    <div className="mySlides fade">
                        <div className="numbertext"></div>
                            <img src={cover1} style="width:100%"/>
                        <div className="text">Caption Two</div>
                    </div>

                    <div className="mySlides fade">
                        <div className="numbertext"></div>
                            <img src={cover2} style="width:100%"/>
                        <div className="text">Caption Three</div>
                    </div>

                    <a className="prev" onclick="plusSlides(-1)">&#10094;</a>
                    <a className="next" onclick="plusSlides(1)">&#10095;</a>

                </div>
                <br/>

                <div style="text-align:center">
                    <span className="dot" onclick="currentSlide(1)"></span> 
                    <span className="dot" onclick="currentSlide(2)"></span> 
                    <span className="dot" onclick="currentSlide(3)"></span> 
                </div>

            </React.Fragment>
     );
    }
}
 
export default Home;
