import React, { Component } from 'react';
import {Link} from 'react-router-dom';

class Product extends Component {
    state = { 
        name: this.props.product.name,
        count: this.props.product.count 
     }

     getclasses(){
         return this.props.product.count === 0
            ? "badge bg-warning m-2"
            :"badge bg-primary m-2";
     }

    //  IncrementHandler = () => {
    //     this.setState({count : this.props.product.count + 1});
    // }

    // Increment = () => {
    //     this.IncrementHandler(2);
    // }

    render() { 
        const { product, onIncrement, onDelete } = this.props;
        return (
        <div className="row">
            <div className="col-2">
                <Link to={`/product/${this.props.product.id}`}><span>{this.props.product.name}</span></Link>
            </div>
            <div className="col">
                <span className={this.getclasses()}>{this.props.product.count}</span>

                <button onClick={() => onIncrement(product)} className="btn btn-primary btn-sn">+</button>
                
                <span style={{cursor : 'pointer'}} onClick={()=> this.props.onDelete(this.props.product)}>
                <i className="fas fa-trash m-2"></i>
                </span>
        </div>
        </div>
    );
    }
}
 
export default Product;
