import React, { Component } from "react";
import './shoppingCart.css';
import line from '../img/line.png';
import Busket from './busket';

class ShoppingCart extends Component {
  handleCheck = () => {
    //Clone
    let receipts = this.state.receipt;
    let products = [...this.state.products];
    //Edit
    products = products.map(p => {
        receipts = receipts + ( p.amount * p.price );
        return receipts;
    });
    console.log(receipts);
    //Set State
    this.setState({ receipts });
};

  render() {
    const {products, onReset, onDelete, onIncrement,  receipt } = this.props;
    return (
      <React.Fragment>
        <div className="body">
          <div className="container text-center"> 
            <h1 className="page-header">Your busket</h1>
            <div className="breakline">
              <img src={line} alt="" width="400" height="60" />
            </div>
            <button
              onClick={onReset}
              className="btn btn-secondary btn-sm m-2"
            >Reset</button>
            {products.map((product) => (
              <Busket
                key={product.id}
                product={product}
                onDelete={onDelete}
                onIncrement={onIncrement}
              />
            ))}
            <button
              onClick={this.state.handleCheck}
              className="btn btn-secondary btn-sm m-2"
            >Get The Check</button>            
            <div>
              <h5>the price is: {receipt}</h5>
            </div>
          </div>
        </div>
      </React.Fragment>
      
    );
  }
}

export default ShoppingCart;
