import React, { Component } from 'react';
import {Link, NavLink} from 'react-router-dom';
import line from '../img/line.png';
import './product.css';
import Cart from './cart';

const Product = (props) => {
    return(
        <React.Fragment>
           <div className="body">
                <div className="container"> 
                    <h1 className="pageheader text-center">Our Products</h1>
                    <div className="breakline text-center">
                        <img src={line} alt="" width="400" height="60" />
                    </div>
                    
                    <table className="table table-hover">
                        <tbody>
                            {props.products.map(product => (
                            <tr className="tablerow" key={product.id}>
                                <td><img className="item-img" src={product.photo} alt="" width="100" height="100"/>
                                </td>
                                    <td className="item-name mx-auto">{product.name}</td>
                                    <td className="item-price">{product.price} LE</td>
                                    <td>
                                    <Cart onClick={props.onClick} product={product} />
                                </td>
                            </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </React.Fragment>
    );
}
 
export default Product;
