import React, { Component } from 'react';
import './product.css';
import Cart from './cart';

const Product = (props) => {
    return(
        <React.Fragment>
            <div className="body">
                <h1>Products</h1>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Name</th>
                            <th scope="col">Price</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                    {props.products.map(product => (
                    <tr key={product.id}>
                    <td>{product.id}</td>
                        <td>{product.name}</td>
                        <td>{product.count}</td>
                        <td>
                        <Cart onClick={props.onClick} product={product} />
                        </td>
                    </tr>
                    ))}
                </tbody>
                </table>
            </div>
        </React.Fragment>
    );
}
 
export default Product;
