import React, { Component } from 'react';
import './busket.css';
import {Link} from 'react-router-dom';


class Busket extends Component {
    getClasses() {
        return this.props.product.amount === 0
          ? "badge badge-warning m-2"
          : "badge badge-primary m-2";
      }
    
      render() {
        const { product, onIncrement, onDelete } = this.props;
        return (
          <div className="row justify-content-center">
            <div className="col-lg-2 col-md-2 col-sm-2">
              <div className="item-img">
                <img className="item-img" src={product.photo} alt=""/>
              </div>
            </div>
            <div className="col-lg-2 col-md-2 col-sm-2">
              <span>
                <Link className="item-name" to={`/products/${product.id}`}>{product.name}</Link>
              </span>
            </div>
            <div className="col-lg-2 col-md-2 col-sm-2">
              <h5 className= {this.getClasses()} >{product.amount}</h5>
            </div>
            <div className="col-lg-2 col-md-2 col-sm-2">
              <div className="item-increment">
                <button
                  onClick={() => onIncrement(product)}
                  className="btn btn-primary btn-sm"
                > + </button>
              </div>
            </div>
            <div className="col-lg-2 col-md-2 col-sm-2">
              <div className="item-delete">
                <span style={{ cursor: "pointer" }} onClick={() => onDelete(product)}>
                  <i className="fas fa-trash m-2" />
                </span>
              </div>
            </div>
          </div>
        );
      }
    }
 
export default Busket;
