import React, { Component } from 'react';
import {Route , Switch , Redirect} from "react-router-dom";
import NavBar from './navbar';
import Home from './home';
import AboutUs from './aboutus';
import Contacts from './contacts';
import ShoppingCart from './shoppingCart';
import Footer from './footer';
import notFound from './notFound';
import ProductDetails from './productDetails';
import Product from './product';


import apple from '../img/apple.jpg';
import broccoli from '../img/broccoli.jpg';
import cabbage from '../img/Cabbage.jpg';
import carrots from '../img/carrots.jpg';
import cucumber from '../img/cucumber.jpg';
import eggplant from '../img/eggplant.jpg';
import lettuce from '../img/lettuce.jpg';
import okra from '../img/okra.jpg';
import tomato from '../img/tomato.jpg';
import papper from '../img/papper.jpg';
import peas from '../img/peas.jpg';
import onion from '../img/onion.jpg';

class App extends Component {
    state = { 
        products:[
            {id:1, name:"Apple", price:15, photo:apple},
            {id:2, name:"Broccoli", price:10, photo:broccoli},
            {id:3, name:"Cabbage", price:8, photo:cabbage},
            {id:4, name:"Carrots", price:8, photo:carrots},
            {id:5, name:"Cucumber", price:5, photo:cucumber},
            {id:6, name:"Eggplant", price:9, photo:eggplant},
            {id:7, name:"Papper", price:2, photo:papper},
            {id:8, name:"Lettuce", price:5, photo:lettuce},
            {id:9, name:"Okra", price:12, photo:okra},
            {id:10, name:"Peas", price:9, photo:peas},
            {id:11, name:"Tomato", price:2, photo:tomato},
            {id:12, name:"Onion", price:4, photo:onion},
        ]
     };
     
    handleReset = () =>{
        //Clone
        let products = [...this.state.products];
        //Edit
        products = products.map(p => {
            p.count = 0 ;
            return p;
        });
        //Set state
        this.setState ({products});
    };

    IncrementHandler = (product) => {
        //Clone
        const products = [...this.state.products];
        const index = products.indexOf(product);
        products[index] = { ...products[index] };
        //Edit
        products[index].count++;
        //Set State
        this.setState({ products });
      };

    // handleDelete = (product) => {
    //     //clone
    //     //edit
    //     //set state

    //     const newProduct = this.state.products.filter((p)=> p.id !== product.id);
    //     this.setState({products: newProduct});
    // }

    handleadd = (product) => {
        //clone
        const products = [...this.state.products];
        const index = products.indexOf(product);
        products= [...products[index]];

        //edit
        //set state
        this.setState({products});
    }

    handleDelete = (product) => {
        //Clone
        const products = [...this.state.products];
        const index = products.indexOf(product);
        //Edit
        products.splice(index, 1);
        //Set State
        this.setState({ products });
      };
      
      handleInCartChange = (product) => {
        //Clone
        const products = [...this.state.products];
        const index = products.indexOf(product);
        products[index] = { ...products[index] };
        //Edit
        products[index].isInCart = !products[index].isInCart;
        //Set State
        this.setState({ products });
      };

    render() { 
        return ( 
            <React.Fragment>
                <NavBar
                    productsCount={this.state.products.length}
                    />
                <main className="container">
                <Switch>
                    <Route path='/home' render={props => (
                        <Home 
                        {...props}
                        products = {this.state.products}
                        onClick={this.handleadd}
                        />
                    )}/>
                    <Route path='/aboutus' component={AboutUs}/>
                    <Route path='/contacts' component={Contacts}/>
                    <Route path='/product/:id/:name?'
                     render={(props) => (
                         <ProductDetails 
                         products={this.state.products}
                         {...props}/>
                         )}/>
                    {/*? optional route parameter*/}

                    <Route path='/products'
                    render={props => (
                        <Product 
                            {...props}
                            products = {this.state.products}
                            onClick={this.handleadd}
                            />
                        )}/>
                    <Route path='/cart' render={(props) =>  (
                        <ShoppingCart
                        products = {this.state.products}
                        onDelete= {this.handleDelete}
                        onReset = {this.handleReset}
                        onIncrement={this.IncrementHandler}
                        {...props}
                        />
                    )}/>
                    <Redirect to='/notFound'/>
                    <Redirect from='/' to='/home'/>
                </Switch>
                <Footer/>
                </main>
            </React.Fragment>
        );
    }
}
 
export default App;
